import { BadRequestException, Injectable } from '@nestjs/common';
import Web3 from 'web3';
import { ConfigService } from '@nestjs/config';
import { AbiItem } from 'web3-utils';

export type EthBalance = {
  ethBalance: number;
  tetherBalance: number;
};

type CreatedAccount = {
  address: string;
  privateKey: string;
};

