@Injectable()
export class EthService {
  private readonly web3: Web3;

  constructor(configService: ConfigService) {
    this.web3 = new Web3(configService.get('ETH_RPC_URL'));
  }

  createAccount(): CreatedAccount {
    const account = this.web3.eth.accounts.create();

    return {
      address: account.address,
      privateKey: account.privateKey,
    };
  }

  async getEthBalance(address: string): Promise<number> {
    const balanceWei = await this.web3.eth.getBalance(address);

    return Number(this.web3.utils.fromWei(balanceWei));
  }

  async getTetherBalance(address: string): Promise<number> {
    return new this.web3.eth.Contract(abi, address).methods
      .balanceOf(address)
      .call()
      .then(this.web3.utils.fromWei)
      .catch((err) => {
        throw new BadRequestException(err?.message ?? err);
      });
  }
}
